import React from "react";
import {
	Input,
	Stack,
	Center,
	NativeBaseProvider,
	Text,
	Pressable,
	HStack,
} from "native-base";
import { AntDesign, MaterialCommunityIcons } from "@expo/vector-icons";
import { colors } from "../contants/colors";
import { Alert } from "react-native";
import { useState } from "react";
import { Spacer } from "./Spacer";

type inputProp = {
	holderText: string;
};

export const TextInput: React.FC<inputProp> = ({ holderText }) => {
	return (
		<Stack>
			<Text>{holderText}</Text>
			<Spacer size={10} />
			<Input variant="rounded" placeholder={holderText} />
		</Stack>
	);
};

export const SearchBar = () => {
	return (
		<Stack>
			<Input
				backgroundColor="#F8F8F8"
				variant="rounded"
				placeholder={"Search"}
				InputRightElement={
					<Pressable
						onPress={() => {
							Alert.alert("Search Pressed");
						}}
					>
						<AntDesign
							name="search1"
							size={24}
							color={colors.primary}
							style={{ paddingEnd: 10 }}
						/>
					</Pressable>
				}
			/>
		</Stack>
	);
};

export const DropDown: React.FC<inputProp> = ({ holderText }) => {
	return (
		<Stack>
			<Text>{holderText}</Text>
			<Input
				variant="rounded"
				placeholder={holderText}
				InputRightElement={
					<Pressable onPress={() => Alert.alert("Arrow clicked")}>
						<MaterialCommunityIcons
							name="chevron-down"
							size={24}
							color={colors.primary}
							style={{ paddingEnd: 10 }}
						/>
					</Pressable>
				}
			/>
		</Stack>
	);
};

export const Location: React.FC<inputProp> = ({ holderText }) => {
	const [state, setState] = useState("unchecked");

	return (
		<Pressable
			onPress={() => {
				state === "checked"
					? setState("unhecked")
					: setState("checked");
			}}
		>
			<HStack>
				<Stack flex={0.5}>
					<MaterialCommunityIcons
						name="map-marker-outline"
						size={24}
						color={state === "checked" ? "black" : "grey"}
						style={{ paddingEnd: 10 }}
					/>
				</Stack>

				<Stack flex={1} alignSelf="flex-start">
					<Text color={state === "checked" ? "black" : "grey"}>
						{holderText}
					</Text>
				</Stack>

				<Stack flex={3} alignItems="flex-end">
					<MaterialCommunityIcons
						name="check"
						size={24}
						color={state === "checked" ? "black" : "transparent"}
						style={{ paddingEnd: 10 }}
					/>
				</Stack>
			</HStack>
		</Pressable>
	);
};

type numberProp = {
	number: number;
};

export const Number: React.FC<numberProp> = () => {
	return (
		<Stack>
			<Input
				size="xl"
				variant="outline"
				keyboardType={"number-pad"}
				maxLength={1}
			/>
		</Stack>
	);
};
