import React from "react";
import {
	Box,
	Center,
	HStack,
	VStack,
	Text,
	View,
	Heading,
	Pressable,
	ScrollView,
} from "native-base";
import UserIcon from "../../assets/icons/User";
import BellIcon from "../../assets/icons/Bell";
import SearchIcon from "../../assets/icons/Search";
import MedicalHistoryIcon from "../../assets/icons/MedicalHistory";

import AppointmentIllustration from "../../assets/illustrations/AppointmentIllustration";
import OnlineConsulationIllustration from "../../assets/illustrations/OnlineConsulationIllustration";
import FacilityIllustration from "../../assets/illustrations/FacilityIllustration";

import { useNavigation } from "@react-navigation/native";
import moment from "moment";

import { HomeNavKey } from ".";
import _BaseContainer from "../../components/containers/_BaseContainer";
import { colors } from "../../contants/colors";
import MainContainer from "../../components/containers/MainContainer";
import { IconContainer } from "../../components/misc";

const helpOptions = [
	{
		illustration: AppointmentIllustration,
		title: "Book an Appointment",
		onNavigate: (navigation: any) =>
			navigation.navigate(HomeNavKey.BookAppointmentViewScreen),
	},
	{
		illustration: OnlineConsulationIllustration,
		title: "Online Consultation",
		onNavigate: (navigation: any) =>
			navigation.navigate(HomeNavKey.OnlineConsultViewScreen),
	},
	{
		illustration: FacilityIllustration,
		title: "Find a Facility",
		onNavigate: (navigation: any) =>
			navigation.navigate(HomeNavKey.MapFaciltyViewScreen),
	},
];
import {
	DemoAppointmentType,
	useAppointmentTempoStore,
} from "../../internals/appointment/context";
import { useQuery } from "react-query";

export default function Home() {
	const navigation = useNavigation();

	return (
		<MainContainer
			leftSection={() => (
				<IconContainer>
					<UserIcon size={6} color="#561BB3" />
				</IconContainer>
			)}
			rightSection={() => (
				<HStack space={4}>
					<Pressable
						onPress={() =>
							navigation.navigate(HomeNavKey.NotificationScreen)
						}
					>
						<IconContainer>
							<BellIcon size={6} color="#561BB3" />
						</IconContainer>
					</Pressable>
					<IconContainer>
						<SearchIcon size={6} color="#561BB3" />
					</IconContainer>
				</HStack>
			)}
		>
			<ScrollView width="100%">
				{/* Welcome section */}
				<VStack space={2} paddingTop={2} marginX={5}>
					<Text color="#B0B3C7" fontSize="md">
						{moment().format("D MMMM YYYY")}
					</Text>
					<Heading fontSize="3xl">Hi, Ally Salim</Heading>
				</VStack>

				{/* Section to render upcoming appointments if any.
					NOTE: To prevent re-rendering, dont use inline if statements
					*/}
				<Box marginX={5}>
					<UpcomingAppointmentsSection />
				</Box>

				<VStack padding={5}>
					<Heading fontSize="xl">How can we help?</Heading>
					<ScrollView
						horizontal={true}
						showsHorizontalScrollIndicator={false}
						alwaysBounceHorizontal
						height={215}
					>
						<HStack
							space={4}
							marginTop={3}
							justifyContent="space-between"
							paddingX={1}
						>
							{helpOptions.map(
								(
									{
										illustration: Illustration,
										onNavigate,
										title,
									},
									ix
								) => (
									<Pressable
										onPress={() => onNavigate(navigation)}
									>
										{/* Find mean to set relative width: 160 -> 33%?? */}
										<Center
											width={140}
											height={200}
											paddingY={3}
											bgColor="#FFF"
											rounded="xl"
											shadow={4}
										>
											<Illustration size={100} />
											<Text
												fontWeight="800"
												marginTop={5}
												textAlign="center"
												// wordBreak="break-word"
												// overflowWrap="break-word"
											>
												{title}
											</Text>
										</Center>
									</Pressable>
								)
							)}
						</HStack>
					</ScrollView>
				</VStack>

				<VStack>
					<View padding={5}>
						<Heading fontSize="xl">Top Rated Specialists</Heading>
					</View>

					<ScrollView
						horizontal={true}
						showsHorizontalScrollIndicator={false}
						alwaysBounceHorizontal
					>
						<HStack justifyContent="space-between" marginX={3}>
							{[
								{
									name: "Dr. Maryam Mohamedali",
									location: "Arusha, Tanzania",
									specialization:
										"Immunology, Gynecology, Internal Medicine",
									color: "#EEE",
									textColor: colors.primary,
								},
								{
									name: "Dr. Wyckliffe Sango",
									color: "#258FBE",
								},
								{
									name: "Dr. Ally Salim",
									color: "#258FBE",
								},
							].map(
								(
									{
										color,
										name,
										location,
										specialization,
										textColor,
									},
									ix
								) => (
									<Box
										key={`trspec-${ix}`}
										paddingX={6}
										paddingY={8}
										bgColor={color}
										rounded="xl"
										marginX={2}
										width={200}
										minHeight={250}
									>
										<VStack>
											<Heading
												fontSize="md"
												color={textColor || "#FFFFFF"}
											>
												{name}
											</Heading>
											<VStack space={1} marginTop={3}>
												<View>
													<Text fontSize="sm">
														{location}
													</Text>
												</View>
												<View>
													<Text fontSize="sm">
														{specialization}
													</Text>
												</View>
											</VStack>
										</VStack>
									</Box>
								)
							)}
						</HStack>
					</ScrollView>
				</VStack>
			</ScrollView>
		</MainContainer>
	);
}

// TODO: find a better place to fetch all the data
const UpcomingAppointmentsSection = () => {
	const getAppointments = useAppointmentTempoStore(
		(state) => state.getAppointments
	);
	const { isLoading, isError, data, error } = useQuery(
		"appointments",
		getAppointments,
		{
			// TODO: to remove this behaviour
			// and instead just fetch either from offline or online state
			refetchInterval: 5000,
		}
	);
	if (isLoading) return <Text>Fetching appointement... </Text>;
	if (error) return <Text>Something went wrong</Text>;
	if (data?.length === 0) return null;

	// console.log("appointment");
	// console.log(data);
	return (
		<VStack space={4} marginTop={8}>
			<Heading fontSize="xl">Upcoming Appointments</Heading>
			{data?.map((appointment) => (
				<UpcomingAppointmentsAlert appointment={appointment} />
			))}
		</VStack>
	);
};

const UpcomingAppointmentsAlert: React.FC<{
	appointment: DemoAppointmentType;
}> = ({ appointment }) => {
	const {
		dateTime: { timeSlots },
		consultant: { name },
	} = appointment;
	return (
		<VStack>
			<HStack
				justifyContent="space-between"
				alignItems="center"
				borderRadius={6}
				borderColor="#B0B3C7"
				borderWidth={1}
				p={4}
			>
				<HStack
					justifyContent="space-between"
					alignItems="center"
					space={4}
				>
					<MedicalHistoryIcon />
					<Text>Meet Dr {name}</Text>
				</HStack>
				{/* TODO: specify the correct time for appointment */}
				<Text color="#258FBE">{timeSlots[0]}</Text>
			</HStack>
		</VStack>
	);
};
